package Practice;

public class _1382 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i = 1; i < 10; i++) {
			for(int j = 2; j < 6; j++) {
				System.out.print(j + " x " + i + " = ");
				System.out.printf("%2d", i*j);
				System.out.print("\t");
			} System.out.println();
		}

	}

}
